export const icons = {
  music:MusicSvg,
  download:DownloadSvg,
  cloud:CloudSvg,
  fm:FMSvg,
  mark:MarkSvg,
  stave:StaveSvg,
  love:LoveSvg,
  previous:PrviousSvg,
  next:NextSvg,
  play:PlaySvg,
  pause:PauseSvg,
  oneRound:OneRoundSvg,
  allRound:AllRoundSvg,
  random:RandomSvg,
  turnList:TurnListSvg,
  start:StartSvg,
  volumeNormal:VolumeNormal,
  volumeSilent:VolumeSilent,
  playList:PlayList,
  collection:Collection,
  delete:Delete,
  source:Source,
  arrowUp:ArrowUp,
  like:Like,
  share:Share,
  comment:Comment,
  arrowLeft:ArrowLeft,
  arrowRight:ArrowRight,
  arrowTop:ArrowTop,
  omit:Omit,
  loading:Loading,
  pencil:Pencil,
  close:Close,
  warn:Warn,
  info:Info,
  success:Success,
  error:Close,
  likeFilled:LikeFilled,
  image
}

export type IconType = 'image'|'music'|'download'|'cloud'|'fm'|'mark'|'stave'|'love'|'previous'|'next'|'play'|'pause'|'oneRound'|'random'|'allRound'|'turnList'|'start'|'volumeNormal'|'volumeSilent'|'playList'|'collection'|'delete'|'source'|'arrowUp'|'like'|'share'|'comment'|'arrowLeft'|'arrowRight'|'omit'|'loading'|'arrowTop'|'pencil'|'close'|'warn'|'info'|'success'|'error'|'likeFilled'

export type IconProps = {
  /**
   * @description 图标类型
   */
  type:IconType
  /**
   * @description 内联样式类型
   */
  style?:React.CSSProperties
  /**
   * @description 自定义的className
   */
  className?:string
  /**
   * @description 点击Icon时的回调,注意:添加onClick后,style的cursor会自动设置为pointer
   */
  onClick?:()=>void
}


const Icon:React.FC<IconProps> = ({type,style,className,onClick})=>{

  return React.cloneElement(icons[type],
    {
      style:{...style,verticalAlign:'baseline',cursor:style?.cursor || ((onClick) ? 'pointer':'default')},
      onClick,
      className
    }
    )
}

export default Icon